    /// <summary>
    ///     Asynchronously adds elements to the OutFields property.
    /// </summary>
    /// <param name="values">
    ///    The elements to add.
    /// </param>
    public async Task AddToOutFields(params string[] values)
    {
        string[] join = OutFields is null
            ? values
            : [..OutFields, ..values];
        await SetOutFields(join);
    }
    
