    /// <summary>
    ///     Asynchronously adds elements to the SelectedItems property.
    /// </summary>
    /// <param name="values">
    ///    The elements to add.
    /// </param>
    public async Task AddToSelectedItems(params ListItem[] values)
    {
        ListItem[] join = SelectedItems is null
            ? values
            : [..SelectedItems, ..values];
        await SetSelectedItems(join);
    }
    
