    /// <inheritdoc />
    protected override async ValueTask<bool> RegisterGeneratedChildComponent(MapComponent child)
    {
        switch (child)
        {
            case ListItem selectedItems:
                SelectedItems ??= [];
                if (!SelectedItems.Contains(selectedItems))
                {
                    SelectedItems = [..SelectedItems, selectedItems];
                    ModifiedParameters[nameof(SelectedItems)] = SelectedItems;
                    if (MapRendered)
                    {
                        await UpdateWidget();
                    }
                }
                
                return true;
            case LayerListViewModel viewModel:
                if (viewModel != ViewModel)
                {
                    ViewModel = viewModel;
                    ModifiedParameters[nameof(ViewModel)] = ViewModel;
                    if (MapRendered)
                    {
                        await UpdateWidget();
                    }
                }
                
                return true;
            case LayerListVisibleElements visibleElements:
                if (visibleElements != VisibleElements)
                {
                    VisibleElements = visibleElements;
                    ModifiedParameters[nameof(VisibleElements)] = VisibleElements;
                    if (MapRendered)
                    {
                        await UpdateWidget();
                    }
                }
                
                return true;
            default:
                return await base.RegisterGeneratedChildComponent(child);
        }
    }

    /// <inheritdoc />
    protected override async ValueTask<bool> UnregisterGeneratedChildComponent(MapComponent child)
    {
        switch (child)
        {
            case ListItem selectedItems:
                SelectedItems = SelectedItems?.Where(s => s != selectedItems).ToList();
                ModifiedParameters[nameof(SelectedItems)] = SelectedItems;
                return true;
            case LayerListViewModel _:
                ViewModel = null;
                ModifiedParameters[nameof(ViewModel)] = ViewModel;
                return true;
            case LayerListVisibleElements _:
                VisibleElements = null;
                ModifiedParameters[nameof(VisibleElements)] = VisibleElements;
                return true;
            default:
                return await base.UnregisterGeneratedChildComponent(child);
        }
    }
    
    /// <inheritdoc />
    public override void ValidateRequiredGeneratedChildren()
    {
    
        if (SelectedItems is not null)
        {
            foreach (ListItem child in SelectedItems)
            {
                child.ValidateRequiredGeneratedChildren();
            }
        }
        ViewModel?.ValidateRequiredGeneratedChildren();
        VisibleElements?.ValidateRequiredGeneratedChildren();
        base.ValidateRequiredGeneratedChildren();
    }
      
}
